#include <range/v3/all.hpp>\n#include <vector>\n\nint main() {\n    std::vector<int> numbers;\n    for (int i = -10; i <= 10; ++i) {\n        numbers.push_back(i);\n    }\n\n    // Using Range-v3 to filter positive numbers\n    auto positive_numbers_view = numbers \n                                 | ranges::views::filter([](int number) { return number > 0; });\n\n    // If you need to store the result in a vector, you can do this:\n    std::vector<int> positive_numbers{positive_numbers_view.begin(), positive_numbers_view.end()};\n\n    return 0;\n}