// MyCppClassWrapper.h\n#pragma once\n\n#include "MyCppClass.h"\n\npublic ref class MyCppClassWrapper {\nprivate:\n    MyCppClass* m_cppInstance;\n\npublic:\n    MyCppClassWrapper() {\n        m_cppInstance = new MyCppClass();\n    }\n\n    ~MyCppClassWrapper() {\n        this->!MyCppClassWrapper();\n    }\n\n    !MyCppClassWrapper() { // Destructor\n        delete m_cppInstance;\n    }\n\n    int Add(int a, int b) {\n        return m_cppInstance->Add(a, b);\n    }\n};