#include <windows.h>\n#include <iostream>\n#include <tchar.h>\n\nvoid listBmpFiles(const std::wstring& directory) {\n    WIN32_FIND_DATA data;\n    HANDLE handle = FindFirstFile((directory + L"\\*.bmp").c_str(), &data);\n\n    if (handle == INVALID_HANDLE_VALUE) {\n        std::cerr << "Failed to search files." << std::endl;\n        return;\n    }\n\n    do {\n        if (!(data.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY)) {\n            wprintf(L"%ls\n", data.cFileName);\n        }\n    } while (FindNextFile(handle, &data));\n\n    FindClose(handle);\n}\n\nint main() {\n    std::wstring directory = L"path_to_your_directory"; // Replace with your directory path\n    listBmpFiles(directory);\n    return 0;\n}