#include <windows.h>\n#include <iostream>\n\nint main() {\n    HMODULE hModule = LoadLibrary(TEXT("mytest.dll"));\n    if (hModule == NULL) {\n        std::cout << "LoadLibrary failed, error code: " << GetLastError() << std::endl;\n        return 1;\n    }\n    std::cout << "DLL loaded successfully." << std::endl;\n\n    // Remember to free the library when no longer needed.\n    FreeLibrary(hModule);\n\n    return 0;\n}