#include <iostream>\n#include <fstream>\n#include <string>\nusing namespace std;\n\nint main(int argc, char* argv[]) {\n    if (argc != 2) {\n        cout << "Error syntax is incorrect!\nSyntax: " << argv[0] << " <file>\n";\n        return 1;\n    }\n    \n    ifstream file(argv[1]);\n    if (!file.is_open()) {\n        cout << "File " << argv[1] << " does not exist.\n";\n        return 1;\n    }\n\n    string linha;\n    while (getline(file, linha)) {\n        // Check if the line starts with "print"\n        if (linha.find("print") == 0) {\n            size_t start = linha.find('"') + 1; // Find the first quote\n            size_t end = linha.rfind('"');       // Find the last quote\n            if (start != string::npos && end != string::npos) {\n                string text = linha.substr(start, end - start); // Extract text between quotes\n                cout << text << endl;\n            } else {\n                cout << "Malformed print statement: " << linha << endl;\n            }\n        }\n    }\n\n    file.close();\n    return 0;\n}