#include <string>\n#include <vector>\n#include <iostream>\n#include <fstream>   // For std::ifstream\n#include <sstream>   // For std::stringstream\n#include <iterator>\n\nint main()\n{\n    std::ifstream file("yourfile.txt");  // Replace with your file name\n\n    if (!file.is_open()) {\n        std::cerr << "Failed to open the file.\n";\n        return 1;\n    }\n\n    std::vector<std::string> results;  // Vector to store all tokens\n    std::string line;\n\n    while (std::getline(file, line)) {    \n        std::stringstream strstr(line);\n        \n        std::istream_iterator<std::string> it(strstr);\n        std::istream_iterator<std::string> end;\n        \n        // Insert the tokens from this line into the results vector\n        results.insert(results.end(), it, end);\n    }\n\n    file.close();  // Good practice to close the file\n\n    // Send all collected tokens to stdout.\n    std::ostream_iterator<std::string> oit(std::cout, " ");\n    std::copy(results.begin(), results.end(), oit);\n\n    return 0;\n}