#include <Windows.h>\n#include <iostream>\n\nint main() {\n    char* data = new char[1024];\n    // Assume some sensitive data is written to 'data'\n\n    DWORD oldProtection;\n    // Make the memory region read-only by changing its protection\n    if (!VirtualProtect(data, 1024, PAGE_READONLY, &oldProtection)) {\n        std::cerr << "Failed to change memory protection" << std::endl;\n        return -1;\n    }\n    \n    // Proceed with normal operations...\n\n    // Restore original memory protections after use\n    VirtualProtect(data, 1024, oldProtection, &oldProtection);\n\n    delete[] data;\n    return 0;\n}