#include <iostream>\n#include <sstream>\n#include <vector>\n\nstd::vector<std::string> splitString(const std::string& str, const char delimiter) {\n    std::vector<std::string> tokens;\n    std::stringstream ss(str);\n    std::string token;\n\n    // Extract each token using the specified delimiter\n    while (std::getline(ss, token, delimiter)) {\n        tokens.push_back(token);  // Add to vector if you want empty elements\n    }\n\n    return tokens;  // Return the vector of tokens\n}\n\nint main() {\n    std::string str = "___this_ is__ th_e str__ing we__ will use__";\n    char delimiter = '_';  // Specifying the delimiter\n\n    std::vector<std::string> result = splitString(str, delimiter);\n\n    // Print each token to verify the results\n    for (size_t i = 0; i < result.size(); ++i) {\n        if (!result[i].empty()) {  // Optionally skip empty strings\n            std::cout << "Token " << i << ": '" << result[i] << "'" << std::endl;\n        }\n    }\n\n    return 0;\n}