#include <windows.h>\n#include <iostream>\n\nbool IsWindowVisible(HWND hwnd) {\n    if (hwnd == NULL) return false;\n    long style = GetWindowLong(hwnd, GWL_STYLE);\n    return (style & WS_VISIBLE) != 0;\n}\n\nint main() {\n    HWND hwnd = FindWindow(NULL, TEXT("Your Window Title Here")); // Replace with the window title\n    if (hwnd) {\n        std::cout << "Window is " << (IsWindowVisible(hwnd) ? "visible" : "not visible") << std::endl;\n    } else {\n        std::cout << "Window not found." << std::endl;\n    }\n    return 0;\n}