#include <locale>\n#include <codecvt>\n\n// Function to convert wstring to string\nstd::string Narrow(const std::wstring & wide) {\n    return std::wstring_convert<std::codecvt_utf8_utf16<wchar_t>>().to_bytes(wide);\n}\n\n// Function to convert string to wstring\nstd::wstring Widen(const std::string & narrow) {\n    return std::wstring_convert<std::codecvt_utf8_utf16<wchar_t>>().from_bytes(narrow);\n}