#include <iostream>\n#include <list>\n\nint main() {\n    std::list<int> myList = {10, 20, 30};\n    auto it = myList.begin();\n    ++it; // Pointing to the second element (value 20)\n\n    // Shuffling elements around does not invalidate iterators\n    myList.splice(myList.end(), myList, myList.begin()); \n    // Moves the first element to the end\n\n    std::cout << "Iterator points to value: " << *it << std::endl; // Should still print 20\n\n    return 0;\n}