#include <iostream>\n#include <string>\n#include <unordered_map>\n\nint main() {\n    std::unordered_map<wchar_t, wchar_t> dictionary = {\n        {L'á', L'a'}, {L'é', L'e'}, {L'í', L'i'}, \n        {L'ó', L'o'}, {L'ú', L'u'}, {L'ñ', L'n'}\n    };\n\n    std::wstring toReplace = L"á-é-í-ó-ú-ñ-á-é-í-ó-ú-ñ";\n    \n    // Iterate over the string and replace each character if it's in the dictionary\n    for (size_t i = 0; i < toReplace.length(); ++i) {\n        auto it = dictionary.find(toReplace[i]);\n        if (it != dictionary.end()) {\n            toReplace[i] = it->second;\n        }\n    }\n\n    // Output the result\n    std::wcout << toReplace << std::endl;\n\n    return 0;\n}