#include <shlobj.h>\n#include <iostream>\n#include <string>\n\nstd::wstring GetUserAppDataPath() {\n    wchar_t pszPath[MAX_PATH];\n    if (SUCCEEDED(SHGetFolderPath(NULL, CSIDL_LOCAL_APPDATA, NULL, 0, pszPath))) {\n        return std::wstring(pszPath) + L"\\YourAppName";\n    }\n    return L"";\n}\n\nint main() {\n    std::wstring path = GetUserAppDataPath();\n    if (!path.empty()) {\n        // You can now use this path to save your encrypted user information\n        std::wcout << L"User App Data Path: " << path.c_str() << std::endl;\n    } else {\n        std::wcerr << L"Failed to get the user application data path." << std::endl;\n    }\n    return 0;\n}