#include <windows.h>\n#include <tchar.h>\n#include <tlhelp32.h>\n#include <iostream>\n\nbool CheckIfProcessRunning(const TCHAR* ProcessName) {\n    HANDLE hSnapshot;\n    PROCESSENTRY32 pe32;\n\n    hSnapshot = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);\n    if (hSnapshot == INVALID_HANDLE_VALUE) {\n        return false;\n    }\n\n    pe32.dwSize = sizeof(PROCESSENTRY32);\n\n    if (!Process32First(hSnapshot, &pe32)) {\n        CloseHandle(hSnapshot);\n        return false;\n    }\n\n    do {\n        if (_tcsicmp(pe32.szExeFile, ProcessName) == 0) {\n            CloseHandle(hSnapshot);\n            return true;\n        }\n    } while (Process32Next(hSnapshot, &pe32));\n\n    CloseHandle(hSnapshot);\n    return false;\n}\n\nint main() {\n    const TCHAR* targetProcess = _T("xyz.exe");\n\n    while (true) {\n        if (CheckIfProcessRunning(targetProcess)) {\n            MessageBox(NULL, _T("xyz.exe has started!"), _T("Notification"), MB_OK | MB_ICONINFORMATION);\n            break; // Exit after notification\n        }\n        Sleep(5000); // Check every 5 seconds\n    }\n\n    return 0;\n}