#include <iostream>\n#include <chrono>\n#include <list>\n\nint main() {\n    std::list<int> myList;\n    for (int i = 0; i < 1000000; ++i) {\n        myList.push_back(i);\n    }\n\n    auto start = std::chrono::high_resolution_clock::now();\n    auto sz = myList.size();\n    auto end = std::chrono::high_resolution_clock::now();\n\n    std::cout << "Size: " << sz << "\n";\n    std::cout << "Time taken: " \n              << std::chrono::duration_cast<std::chrono::nanoseconds>(end - start).count() \n              << " nanoseconds\n";\n\n    return 0;\n}