#include <random>\n#include <iostream>\n\nuint32_t generateSimpleUID() {\n    static std::random_device rd;  // Obtain a random number from hardware\n    static std::mt19937 eng(rd()); // Seed the generator\n    std::uniform_int_distribution<> distr(0, UINT32_MAX); // Define possible range for uint32_t\n\n    return static_cast<uint32_t>(distr(eng));\n}\n\nint main() {\n    for (int i = 0; i < 5; ++i) {\n        std::cout << "Generated UID: " << generateSimpleUID() << std::endl;\n    }\n    return 0;\n}