#include <iostream>\n\nclass MyClass {\npublic:\n    void method0() { \n        alwaysRun(); \n        // Actual functionality here...\n        std::cout << "method0 called" << std::endl;\n    }\n\n    void method1() {\n        alwaysRun();\n        // Actual functionality here...\n        std::cout << "method1 called" << std::endl;\n    }\n    \n    // Add more methods as needed, calling `alwaysRun()` at the start\n\nprivate:\n    void alwaysRun() { \n        // This function will run before any method\n        std::cout << "Running alwaysRun()" << std::endl; \n    }\n};\n\nint main() {\n    MyClass obj;\n    obj.method0();\n    obj.method1();\n\n    return 0;\n}