// Example: BFS in C++\n   #include <iostream>\n   #include <vector>\n   #include <queue>\n\n   void bfs(int s, const std::vector<std::vector<int>> &graph) {\n       int n = graph.size();\n       std::vector<bool> visited(n, false);\n       std::queue<int> q;\n\n       visited[s] = true;\n       q.push(s);\n\n       while (!q.empty()) {\n           int u = q.front();\n           q.pop();\n\n           std::cout << "Visited node: " << u << std::endl;\n\n           for (int v : graph[u]) {\n               if (!visited[v]) {\n                   visited[v] = true;\n                   q.push(v);\n               }\n           }\n       }\n   }\n\n   int main() {\n       // Use the graph defined earlier\n       std::vector<std::vector<int>> graph(5, std::vector<int>());\n\n       graph[0].push_back(1); graph[0].push_back(4);\n       graph[1].push_back(2);\n       graph[2].push_back(3);\n       graph[3].push_back(4);\n\n       bfs(0, graph);\n\n       return 0;\n   }