#include <windows.h>\n#include <iostream>\n\nbool IsTypeLibraryRegistered(const std::wstring& tlbGuid) {\n    HKEY hKey = nullptr;\n    LONG result = RegOpenKeyEx(HKEY_CLASSES_ROOT, tlbGuid.c_str(), 0, KEY_READ, &hKey);\n\n    if (result == ERROR_SUCCESS) {\n        // The key exists\n        RegCloseKey(hKey);\n        return true;\n    }\n\n    return false;\n}\n\nint main() {\n    std::wstring tlbGuid = L"{Your-Type-Library-GUID}"; // Replace with your Type Library GUID\n\n    if (IsTypeLibraryRegistered(tlbGuid)) {\n        std::wcout << L"Type library is registered." << std::endl;\n    } else {\n        std::wcout << L"Type library is not registered." << std::endl;\n        // You can register the type library here using a shell command or another method\n    }\n\n    return 0;\n}