#include <iostream>\n#include <vector>\n\nint main() {\n    // Using integers (4 bytes each)\n    std::vector<int> intValues(1000);\n    for (int i = 0; i < intValues.size(); ++i) {\n        intValues[i] = i % 32768; // Values within short range\n    }\n\n    // Using shorts (2 bytes each)\n    std::vector<short> shortValues(1000);\n    for (int i = 0; i < shortValues.size(); ++i) {\n        shortValues[i] = i % 32768;\n    }\n\n    return 0;\n}