#include "disphelper.h"\n#include <stdio.h>\n\nDH_DECLARE("DShell.Application");\n\nint main()\n{\n    DH_OBJECT(DShell_Application, shell);\n\n    if (FAILED(dhInitialize(TRUE)))\n        return -1;\n\n    // Creating the Shell object.\n    DH_CREATE(OBJID_Application, CLSID_Shell, NULL, &shell);\n    \n    // Opening a new Internet Explorer window and navigating to a URL\n    dhCallMethod(shell, L"ShellExecute(%s, %s)", "http://www.example.com", "");\n\n    dhUninitialize(TRUE);\n\n    return 0;\n}