#include <windows.h>\n\nHHOOK h_hook = NULL;\n\nLRESULT CALLBACK KeyboardProc(int nCode, WPARAM wParam, LPARAM lParam) {\n    if (nCode >= 0) {\n        KBDLLHOOKSTRUCT *p = (KBDLLHOOKSTRUCT *)lParam;\n        // Your logic here for key presses\n        if (wParam == WM_KEYDOWN) {\n            switch(p->vkCode) {\n                case 'F1': \n                    // Example function call on F1 press\n                    MessageBox(NULL, "F1 Key Pressed", "Notification", MB_OK);\n                    break;\n                // Add cases as needed\n            }\n        }\n    }\n    return CallNextHookEx(h_hook, nCode, wParam, lParam);\n}\n\nextern "C" __declspec(dllexport) void InstallHook() {\n    h_hook = SetWindowsHookEx(WH_KEYBOARD_LL, KeyboardProc, GetModuleHandle(NULL), 0);\n}\n\nextern "C" __declspec(dllexport) void RemoveHook() {\n    if (h_hook) UnhookWindowsHookEx(h_hook);\n}\n\nBOOL APIENTRY DllMain(HMODULE hModule, DWORD ul_reason_for_call, LPVOID lpReserved) {\n    switch(ul_reason_for_call) {\n        case DLL_PROCESS_ATTACH:\n        case DLL_THREAD_ATTACH:\n            break;\n\n        case DLL_THREAD_DETACH:\n        case DLL_PROCESS_DETACH:\n            RemoveHook();\n            break;\n    }\n    return TRUE;\n}