#include <iostream>\n#include <vector>\n\nstd::vector<int> Foo() {\n    std::vector<int> result;\n    result.push_back(1);\n    return result; // Move semantics are applied here\n}\n\nstd::vector<int> Bar() {\n    return Foo(); // Move semantics are also applied here\n}\n\nint main() {\n    std::vector<int> vec = Bar();\n    std::cout << "Vector contains: " << vec[0] << std::endl;\n    return 0;\n}