#include <comdef.h>\n#include <iostream>\n\n// Define a macro for checking HRESULT and logging errors\n#define CHECK_HRESULT(code, message) \\n    do { \\n        HRESULT hr = code; \\n        if (FAILED(hr)) { \\n            std::cerr << "Error: " << message << ". HRESULT=0x" << std::hex << hr << std::dec << std::endl; \\n            _com_error err(hr); \\n            std::cerr << "Description: " << err.ErrorMessage() << std::endl; \\n            exit(EXIT_FAILURE); \\n        } \\n    } while (false)\n\nint main() {\n    HRESULT hresult = SomeCOMMethod();\n\n    CHECK_HRESULT(hresult, "Failed to perform COM operation");\n\n    std::cout << "COM operation successful." << std::endl;\n\n    return 0;\n}