#include <comdef.h> // For _bstr_t and SysFreeString\n\nBSTR GetSomeString() {\n    // Assume this method returns a BSTR allocated with ::SysAllocString or similar.\n}\n\nvoid UsageExample() {\n    BSTR bstrValue = GetSomeString();\n    \n    if (bstrValue != NULL) {\n        wprintf(L"Received string: %s\n", bstrValue);\n        \n        // After using the BSTR, free it\n        SysFreeString(bstrValue);\n    }\n}