#include <iostream>\n#include <functional>\n\nclass Fred {\npublic:\n    void action(char c, float f) {\n        std::cout << "Action called with: " << c << " and " << f << std::endl;\n    }\n};\n\nint main() {\n    Fred myFred;\n\n    // Creating a function object using lambda to hold the instance and member function\n    std::function<void(char, float)> callback = [&myFred](char c, float f) {\n        myFred.action(c, f);\n    };\n\n    // Using the callback\n    callback('A', 3.14f);\n\n    return 0;\n}