#include <QString>\n#include <QSettings>\n#include <QDebug>\n\nint main() {\n    // Correct path should point directly to the CurrVer key under PowerPoint.Application\n    QSettings settings("HKEY_CLASSES_ROOT\\PowerPoint.Application\\CurrVer", QSettings::NativeFormat);\n    \n    // Reading the default value, which is unnamed in the registry\n    QString sReturnedValue = settings.value("", "").toString();\n    \n    if (sReturnedValue.isEmpty()) {\n        qWarning() << "Failed to read PowerPoint version.";\n    } else {\n        qDebug() << "PowerPoint Version:" << sReturnedValue;\n    }\n    \n    return 0;\n}