#include <windows.h>\n#include <iostream>\n\n// Function prototype, typically found in a third-party header file\ntypedef int (*MYFUNCTION)(int, int);\n\nint main() {\n    // Load the DLL\n    HMODULE hModule = LoadLibrary(TEXT("mythirdpartydll.dll"));\n    if (!hModule) {\n        std::cerr << "Failed to load DLL!" << std::endl;\n        return EXIT_FAILURE;\n    }\n\n    // Get function pointer for function in DLL\n    MYFUNCTION myFunction = (MYFUNCTION)GetProcAddress(hModule, "MyFunctionName");\n    if (!myFunction) {\n        FreeLibrary(hModule);\n        std::cerr << "Failed to find function!" << std::endl;\n        return EXIT_FAILURE;\n    }\n\n    // Call the function from the DLL\n    int result = myFunction(5, 10);\n\n    // Clean up\n    FreeLibrary(hModule);\n    std::cout << "Result: " << result << std::endl;\n\n    return EXIT_SUCCESS;\n}