#include <windows.h>\n#include <comdef.h>\n\nint main() {\n    // Initialize a COM library on this thread.\n    // COINIT_APARTMENTTHREADED specifies single-threaded apartments (STA).\n    HRESULT hr = CoInitializeEx(NULL, COINIT_APARTMENTTHREADED);\n    \n    if (FAILED(hr)) {\n        wprintf(L"Failed to initialize the COM library. Error code = 0x%lx", hr);\n        return -1;\n    }\n\n    // Your COM-dependent code here\n\n    // Uninitialize the COM library on this thread.\n    CoUninitialize();\n\n    return 0;\n}