#include <vcl.hpp>\n#include <CSV.hpp> // This header is needed for TCsvDataSet\n\nvoid __fastcall TForm1::LoadFromFile(void) {\n    CsvDataSet1->FileName = "data.csv";\n    CsvDataSet1->Open();\n}\n\nvoid __fastcall TForm1::SaveToFile(void) {\n    CsvDataSet1->FlushBuffers(); // Ensure all changes are written\n    CsvDataSet1->Close(); // Close and save the file\n}\n\nvoid __fastcall TForm1::FormCreate(TObject *Sender) {\n    LoadFromFile();\n}\n\nvoid __fastcall TForm1::FormDestroy(TObject *Sender) {\n    SaveToFile();\n}