#include <iostream>\n\nclass Foo {\npublic:\n    void f1() { INNER_FUNCTION_CALL(::f1); }\n    void f2() { INNER_FUNCTION_CALL(::f2); }\n    // Repeat for all other functions...\n\nprivate:\n#define INNER_FUNCTION_CALL(func) \\nfunc();                         \\npostFunction();\n\n    void postFunction() { std::cout << "Post function call\n"; }\n};\n\n// Example standalone functions\nvoid ::f1() { std::cout << "Executing f1\n"; }\nvoid ::f2() { std::cout << "Executing f2\n"; }\n\nint main() {\n    Foo foo;\n    foo.f1();\n    foo.f2();\n}