#include <soci/soci.h>\n#include <soci/odbc/soci-odbc.h>\n#include <iostream>\n\nint main() {\n    try {\n        // Connection string for MS SQL Server\n        std::string dsn = "DRIVER={SQL Server};SERVER=localhost;DATABASE=YourDatabaseName;";\n        soci::session sql(soci::odbc, dsn);\n\n        std::cout << "Connected to the database.\n";\n\n        // Example query\n        int count;\n        sql << "SELECT COUNT(*) FROM YourTableName", soci::use(count);\n        std::cout << "Row count: " << count << "\n";\n\n    } catch (std::exception &e) {\n        std::cerr << "Error: " << e.what() << '\n';\n        return 1;\n    }\n\n    return 0;\n}