#include <iostream>\n#include <vcclr.h>\n\nusing namespace System;\nusing namespace CliBridge;\n\nextern "C" DLL_EXPORT void doCalc(double* calcIn, int numIn, double* calcOut) {\n    // Create managed array from unmanaged input\n    cli::array<double>^ managedInput = gcnew cli::array<double>(numIn);\n    for(int i = 0; i < numIn; i++) { managedInput[i] = calcIn[i]; }\n\n    // Use the CLI bridge to process the array\n    ArrayCalculator^ calculator = gcnew ArrayCalculator();\n    cli::array<double>^ managedOutput = calculator->ProcessArray(managedInput);\n\n    // Copy results back to unmanaged output\n    for(int i = 0; i < numIn; i++) { calcOut[i] = managedOutput[i]; }\n}