// Wrapper.cpp - Compile with VC8\nextern "C" __declspec(dllexport) int physxFunctionWrapper() {\n    // Call original PhysX function\n    return physx::PxGetSDKFoundation()->getVersion();\n}\n\n// MainApp.cpp - Compile with VC9\n#include <windows.h>\ntypedef int (*PhysXFunc)();\nint main() {\n    HMODULE hModule = LoadLibrary(TEXT("Wrapper.dll"));\n    if (hModule) {\n        PhysXFunc func = (PhysXFunc)GetProcAddress(hModule, "physxFunctionWrapper");\n        if (func) {\n            int version = func();\n            printf("PhysX Version: %d\n", version);\n        }\n        FreeLibrary(hModule);\n    }\n    return 0;\n}