#include <iostream>\n#include <fstream>\n#include <vector>\n#include <string>\n#include <cstdlib>  // For std::srand, std::rand\n#include <ctime>    // For std::time\n\nstd::string getRandomWord(const std::string& filename) {\n    std::ifstream file(filename);\n    if (!file.is_open()) {\n        throw std::runtime_error("Could not open the file");\n    }\n\n    std::vector<std::string> words;\n    std::string word;\n\n    // Read each word from the file and add it to the vector\n    while (file >> word) {\n        words.push_back(word);\n    }\n\n    if (words.empty()) {\n        throw std::runtime_error("The file is empty");\n    }\n\n    // Seed the random number generator with current time\n    std::srand(static_cast<unsigned int>(std::time(0)));\n\n    // Get a random index\n    int randomIndex = std::rand() % words.size();\n\n    return words[randomIndex];\n}\n\nint main() {\n    try {\n        std::string filename = "words.txt";  // Change to your file name\n        std::string word = getRandomWord(filename);\n        std::cout << "Randomly selected word: " << word << std::endl;\n    } catch (const std::exception& e) {\n        std::cerr << "Error: " << e.what() << std::endl;\n        return 1;\n    }\n\n    return 0;\n}