#include <string>\n#include <algorithm>\n\nstd::string sanitizeFilename(const std::string& original) {\n    std::string sanitized = original;\n    // Replace invalid characters with an underscore '_'\n    for (auto& ch : sanitized) {\n        if (ch < 32 || ch == '<' || ch == '>' || ch == ':' || ch == '"' || \n            ch == '/' || ch == '\\' || ch == '|' || ch == '?' || ch == '*') {\n            ch = '_';\n        }\n    }\n    return sanitized;\n}