#include <windows.h>\n#include <Psapi.h>\n#include <tchar.h>\n\n#pragma comment(lib, "psapi.lib")\n\nvoid GetProcessFileName(HANDLE hProcess) {\n    TCHAR processPath[MAX_PATH] = TEXT("");\n    DWORD returnLength;\n\n    if (QueryFullProcessImageName(hProcess, 0, processPath, &returnLength)) {\n        _tprintf(TEXT("Process Path: %s\n"), processPath);\n    } else {\n        _tprintf(TEXT("Unable to retrieve path. Error: %lu\n"), GetLastError());\n    }\n}\n\nint main() {\n    // Example usage:\n    DWORD pid = 4; // Change this to the actual process ID you want to inspect\n    HANDLE hProcess = OpenProcess(PROCESS_QUERY_INFORMATION | PROCESS_VM_READ, FALSE, pid);\n\n    if (hProcess != NULL) {\n        GetProcessFileName(hProcess);\n        CloseHandle(hProcess);\n    } else {\n        _tprintf(TEXT("Failed to open process. Error: %lu\n"), GetLastError());\n    }\n\n    return 0;\n}