// Pointer to the managed interface\nDatabasePtr pODB(__uuidof(DBClass));\n\n// Get the string[] array from the managed method\nSAFEARRAY* safearray = pODB->GetStringArray();\n\nHRESULT hresult;\n\nlong ubound;\nlong lbound;\n\nhresult = SafeArrayGetUBound(safearray, 1, &ubound);\nhresult = SafeArrayGetLBound(safearray, 1, &lbound);\n\nlong index;\nBSTR fromarray;\n\nfor (; lbound <= ubound; lbound++)\n{\n    index = lbound;\n    hresult = SafeArrayGetElement(safearray, &index, (void*)&fromarray);\n    \n    // Convert BSTR to LPCSTR (const char*)\n    char buffer[512] = {0};\n    wcstombs(buffer, fromarray, sizeof(buffer) - 1);\n\n    MessageBox(0, buffer, "...", 0);\n\n    // Free the BSTR after use\n    SysFreeString(fromarray);\n}\n\n// Free the SAFEARRAY if it needs to be released (not typically required for interfaces)