// Initialize stacks and memory array\nStack dataStack;\nMemory mem;\n\n// Primitive operations\nvoid PUSH(int value) {\n    dataStack.push(value);\n}\n\nint POP() {\n    return dataStack.pop();\n}\n\nvoid DUP() {\n    int top = dataStack.top();\n    dataStack.push(top);\n}\n\nvoid SWAP() {\n    int top1 = dataStack.pop();\n    int top2 = dataStack.pop();\n    dataStack.push(top1);\n    dataStack.push(top2);\n}\n\nvoid FETCH() {\n    int addr = dataStack.pop();\n    int value = mem[addr];\n    dataStack.push(value);\n}\n\nvoid STORE() {\n    int value = dataStack.pop();\n    int addr = dataStack.pop();\n    mem[addr] = value;\n}