#include <windows.h>\n#include <fstream>\n\nvoid WriteLog(const std::wstring& message) {\n    std::wofstream file("C:\\ServiceLogs\\service.log", std::ios::app);\n    if (file.is_open()) {\n        file << message + L"\n";\n        file.close();\n    }\n}\n\nint __cdecl wmain(DWORD argc, LPWSTR* argv) {\n    // Example of logging an error\n    WriteLog(L"Starting service...");\n\n    try {\n        // Service initialization code here...\n    } catch (const std::exception& e) {\n        WriteLog(L"Exception caught: " + std::wstring(e.what()));\n    }\n\n    return 0;\n}