#include <iostream>\n#include <list>\n#include <cmath>\n\nstd::list<Point*> findPointsWithinRadius(std::list<Point>& pointList, int centerX, int centerY, double radius) {\n    std::list<Point*> result;\n    Point center(centerX, centerY);\n\n    for (auto& p : pointList) {\n        if (distance(p, center) <= radius) {\n            result.push_back(&p);\n        }\n    }\n\n    return result;\n}\n\nint main() {\n    std::list<Point> pointList;\n    pointList.push_back(Point(1, 2));\n    pointList.push_back(Point(5, 7));\n    pointList.push_back(Point(8, 9));\n\n    auto pointsWithinRadius = findPointsWithinRadius(pointList, 5, 6, 3);\n\n    for (auto& p : pointsWithinRadius) {\n        std::cout << "(" << p->x << ", " << p->y << ")\n";\n    }\n\n    return 0;\n}