#include <iostream>\n#include <cstdint>\n\nint main() {\n    uint32_t max_32bit_value = UINT32_MAX;\n    uint64_t max_64bit_value = UINT64_MAX;\n\n    std::cout << "Max 32-bit unsigned integer: " << max_32bit_value << '\n';\n    std::cout << "Max 64-bit unsigned integer: " << max_64bit_value << '\n';\n\n    // Demonstrating operations with large numbers\n    uint64_t large_number = UINT32_MAX + 10;\n    if (large_number > max_32bit_value) {\n        std::cout << "64-bit handles larger values without overflow: " << large_number << '\n';\n    }\n\n    return 0;\n}