#include <CGAL/Exact_predicates_exact_constructions_kernel.h>\n#include <CGAL/Polygon_2.h>\n#include <CGAL/minkowski_sum_2.h>\n\ntypedef CGAL::Exact_predicates_exact_constructions_kernel Kernel;\ntypedef Kernel::Point_2                                   Point_2;\ntypedef CGAL::Polygon_2<Kernel>                         Polygon_2;\n\nint main(){\n  // Create a square.\n  Polygon_2 P;\n  P.push_back(Point_2(0,0));\n  P.push_back(Point_2(1,0));\n  P.push_back(Point_2(1,1));\n  P.push_back(Point_2(0,1));\n\n  // Create a circle with radius 0.5.\n  double r = 0.5;\n  Polygon_2 offset_circle;\n  for(int i=0; i<36; ++i){\n    double x = r*cos(i*CGAL_PI/18);\n    double y = r*sin(i*CGAL_PI/18);\n    offset_circle.push_back(Point_2(x,y));\n  }\n\n  // Compute Minkowski sum.\n  Polygon_with_holes_2 result;\n  CGAL::minkowski_sum_by_offsetting(P, offset_circle, result);\n\n  return 0;\n}