#include <iostream>\n#include <bitset>\n\nvoid printConversions(int num) {\n    std::cout << "Decimal:   " << std::dec << num << std::endl;\n    std::cout << "Hexadecimal: " << std::hex << num << std::endl;\n    // Assuming the number is within 8 bits, adjust as needed\n    std::cout << "Binary:    " << std::bitset<8>(num) << std::endl;\n}\n\nint main() {\n    int number = 42;\n    printConversions(number);\n\n    return 0;\n}