#include "tiny_gltf.h"\n#include <iostream>\n\nint main() {\n    tinygltf::Model model;\n    tinygltf::TinyGLTF loader;\n    std::string err;\n\n    // Load a glb file\n    bool ret = loader.LoadBinaryFromFile(&model, &err, "path/to/model.glb");\n\n    if (!ret) {\n        std::cerr << "Failed to load gltf: " << err << "\n";\n        return -1;\n    }\n\n    std::cout << "Loaded model successfully\n";\n\n    // Here you would parse the model.data and construct your objects in the game engine\n\n    return 0;\n}