#include <iostream>\n\nint main() {\n    char ch = 'A';\n    int asciiValue = static_cast<int>(ch);\n    std::cout << "The ASCII value of '" << ch << "' is " << asciiValue << std::endl;\n\n    // Example with space character\n    char spaceChar = ' ';\n    int spaceAsciiValue = static_cast<int>(spaceChar);\n    std::cout << "The ASCII value of a space is " << spaceAsciiValue << std::endl;\n\n    return 0;\n}