#include <iostream>\n#include <vector>\n#include <unordered_map>\n\nint main() {\n    const int x = 3;\n    const int y = 11;\n\n    // Example input array with duplicates and missing values\n    std::vector<int> myVector = {4, 5, 5, 5, 7, 8, 9, 10, 10};\n\n    // Vectors to store results\n    std::vector<int> missingValues;\n    std::vector<int> duplicates;\n\n    // Map to count occurrences of each number\n    std::unordered_map<int, int> countMap;\n\n    // Count occurrences of each number in the array\n    for (int num : myVector) {\n        countMap[num]++;\n    }\n\n    // Determine missing and duplicate numbers\n    for (int i = x; i <= y; ++i) {\n        if (countMap[i] == 0) {\n            missingValues.push_back(i); // Add to missing values if number is not in map\n        } else if (countMap[i] > 1) {\n            for (int j = 1; j < countMap[i]; ++j) {\n                duplicates.push_back(i); // Add extra occurrences to duplicates\n            }\n        }\n    }\n\n    // Output the results\n    std::cout << "Missing values: ";\n    for (int num : missingValues) {\n        std::cout << num << " ";\n    }\n    std::cout << std::endl;\n\n    std::cout << "Duplicates: ";\n    for (int num : duplicates) {\n        std::cout << num << " ";\n    }\n    std::cout << std::endl;\n\n    return 0;\n}