#include <vector>\n#include <fstream>\nstd::ofstream outfile("out.txt");\nstd::vector<std::string> results;\n\nfor (loop) {\n    SliceStep::DoStep(); \n    // instead of writing immediately, store in a vector\n    results.push_back(result);\n}\n\n// write all results at once after the loop completes\nfor (const auto& result : results) {\n    outfile << result;\n}\noutfile.close();