#include <windows.h>\n#include <iostream>\n\nbool createLargeSparseFile(const std::wstring& filePath, ULONGLONG fileSize) {\n    HANDLE hFile = CreateFile(\n        filePath.c_str(),\n        GENERIC_WRITE,\n        0,\n        NULL,\n        CREATE_NEW,\n        FILE_ATTRIBUTE_NORMAL | FILE_FLAG_NO_BUFFERING | FILE_FLAG_RANDOM_ACCESS,\n        NULL);\n\n    if (hFile == INVALID_HANDLE_VALUE) {\n        std::wcerr << L"Could not create file: " << GetLastError() << std::endl;\n        return false;\n    }\n\n    LARGE_INTEGER largeIntFileSize;\n    largeIntFileSize.QuadPart = fileSize;\n\n    BOOL result = SetFilePointerEx(hFile, largeIntFileSize, NULL, FILE_BEGIN);\n\n    if (result) {\n        result = SetEndOfFile(hFile);\n        if (!result) {\n            std::wcerr << L"Could not set file size: " << GetLastError() << std::endl;\n        }\n    } else {\n        std::wcerr << L"Could not move file pointer: " << GetLastError() << std::endl;\n    }\n\n    CloseHandle(hFile);\n    return result != FALSE;\n}\n\nint main() {\n    // Example usage: create a 1 GB file\n    ULONGLONG fileSize = 1024 * 1024 * 1024; // 1 GB\n    if (createLargeSparseFile(L"C:\\path\\to\\largefile.dat", fileSize)) {\n        std::wcout << L"Successfully created large file." << std::endl;\n    }\n    return 0;\n}