#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Find the window you want to send the message to.\n    // You need to know either the window title, class name, PID, etc.\n    HWND hwndTarget = FindWindow(NULL, L"Your Application Title");\n\n    if (hwndTarget != NULL) {\n        // Sending WM_QUERYENDSESSION\n        LRESULT result = SendMessage(hwndTarget, WM_QUERYENDSESSION, 0, 0);\n        \n        std::wcout << L"WM_QUERYENDSESSION sent to the target window with result: " \n                   << result << std::endl;\n    } else {\n        std::cerr << "Window not found." << std::endl;\n    }\n\n    return 0;\n}